public class SquareRoot {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double a;
		Scanner sc = new Scanner(System.in);
		System.out.print("Enter a number: ");
		a=sc.nextDouble();
		
		System.out.println(Math.sqrt(a));

	}

}
